import { GET_PROFILE, PROFILE_ERROR } from '../actions/types';
const initialState = {
  profile: null,
  profiles: [],
  reposr: [],
  loading: true,
  error: {},
};

const profileReducer = (state = initialState, action) => {
  const { type, payload } = action;

  switch (type) {
    case GET_PROFILE:
      return { ...state, profile: payload };

    default:
      return state;
  }
};

export default profileReducer;
