import { post } from 'request';
import { GETPOSTS, POST_ERROR, UPDATE_LIKES } from '../actions/types';

const initialState = {
  posts: [],
  post: null,
  loading: true,
  error: [],
};

const postReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case GETPOSTS:
      return { ...state, posts: payload, loading: false };
    case POST_ERROR:
      return { ...state, error: payload, loading: false };
    case UPDATE_LIKES:
      return { ...state ,
      posts : state.post.map(post=>post_id === payload.postId)
    };
    default:
      return state;
  }
};

export default postReducer;
